import { useState } from 'react';

const pans = {
    rectangular: [
        { length: 8, width: 8, height: 2 },
        { length: 9, width: 9, height: 2 },
        { length: 10, width: 10, height: 2 },
        { length: 11, width: 7, height: 2 },
        { length: 13, width: 9, height: 2 }
    ],
    round: [
        { diameter: 6, height: 2 },
        { diameter: 9, height: 2 },
        { diameter: 9, height: 2 }
    ],
    springform: [
        { diameter: 9, height: 2.5 },
        { diameter: 10, height: 2.5 }
    ],
    bundt: [
        { diameter: 10, height: 3 }
    ],
    tube: [
        { diameter: 9, height: 3 }
    ],
    loaf: [
        { length: 8, width: 4, height: 3 },
        { length: 9, width: 5, height: 3 }
    ],
};

function calculateRectangleVolume() {
    return 10;
}

// times .125 floored

export default function PanSizer() {
    const [ fromShape, setFromShape ] = useState('rectangular');

    function shapeDimensionList1() {
        return (
            <ul>
                {pans.rectangular.map((pan, index) => (
                    <li key={index}>{`${pan.length} x ${pan.width} x ${pan.height}`}</li>
                ))}
            </ul>
        );
    }

    function dimensionOptions(type) {
        const dimensions = [];

        switch(type) {
            case 'rectangular':
                return pans.rectangular.map((pan, index) => (
                    <option key={index} value={`${pan.length}x${pan.width}x${pan.height}`}>
                        {`${pan.length} x ${pan.width} x ${pan.height}`}
                    </option>
                ));
        }
    }

    function handleShapeChange(event) {
        setFromShape(event.target.value);

        // Update default original dimension options for chosen shape
        // var volume = 0;
        var sizes = [];

        switch(event.target.value) {
            case 'rectangular':
                const rectangularListItems = pans.rectangular.map((pan) => {
                    return `<li>${pan.length} x ${pan.width} x ${pan.height}</li>`;
                });
                return `<ul>${rectangularListItems.join('')}</ul>`;
        }
    }

    return (
        <section>
            <h2 className="form-title">Pan Sizer</h2>
            <form className="basic-form">
                <p>
                    <label for="shape-from">From:</label>
                    <select id="shape-from" name="shape-from" value={fromShape} onChange={handleShapeChange}>
                        <option value="rectangular">Square/Rectangular</option>
                        <option value="round">Round</option>
                        <option value="springform">Springform</option>
                        <option value="loaf">Loaf</option>
                        <option value="bundt">Bundt</option>
                        <option value="tube">Tube</option>
                    </select>

                    {/* <label for="shape-to">To:</label>
                    <select id="shape-to" name="shape-to">
                        <option value="rectangular">Square/Rectangular</option>
                        <option value="round">Round</option>
                        <option value="springform">Springform</option>
                        <option value="loaf">Loaf</option>
                        <option value="bundt">Bundt</option>
                        <option value="tube">Tube</option>
                        <option value="all">All</option>
                    </select> */}
                </p>

                <p>
                    <label for="shape-original-dimensions">Original Dimensions:</label>
                    <select id="shape-original-dimensions" name="shape-original-dimensions">
                        {dimensionOptions({fromShape})}
                        {/* <option value="rectangular">Square/Rectangular</option>
                        <option value="round">Round</option>
                        <option value="springform">Springform</option>
                        <option value="loaf">Loaf</option>
                        <option value="bundt">Bundt</option>
                        <option value="tube">Tube</option> */}
                    </select>
                </p>
                {/* <button onClick={() => handleShapeChange('rectangular')}>Calculate</button> */}


            </form>
        </section>
    );
}
