import { useState } from 'react';

const pans = {
    rectangular: [
        { length: 8, width: 8, height: 2 },
        { length: 9, width: 9, height: 2 },
        { length: 10, width: 10, height: 2 },
        { length: 11, width: 7, height: 2 },
        { length: 13, width: 9, height: 2 }
    ],
    round: [
        { diameter: 6, height: 2 },
        { diameter: 9, height: 2 },
        { diameter: 9, height: 2 }
    ],
    springform: [
        { diameter: 9, height: 2.5 },
        { diameter: 10, height: 2.5 }
    ],
    bundt: [
        { diameter: 10, height: 3 }
    ],
    tube: [
        { diameter: 9, height: 3 }
    ],
    loaf: [
        { length: 8, width: 4, height: 3 },
        { length: 9, width: 5, height: 3 }
    ],
};

function dimensionOptions(type) {
    return pans[type].map(({ length, width, height }) => ({
        length,
        width,
        height,
        label: `${length} x ${width} x ${height}`
    }));
}

export default function PanSizer() {
    const [fromShape, setFromShape] = useState('rectangular');
    const [selectedDimensions, setSelectedDimensions] = useState(null);
    const originalDimensions = dimensionOptions(fromShape);

    function handleShapeChange(event) {
        const shapeType = event.target.value;
        setFromShape(shapeType);
    }

    function handleDimensionChange(event) {
        const selectedIndex = event.target.value;
        const dimension = originalDimensions[selectedIndex];
        setSelectedDimensions(dimension);
    }

    return (
        <section>
            <h2 className="form-title">Pan Sizer</h2>
            <form className="basic-form">
                <p>
                    <label>Shape: </label>
                    <select id="shape-from" name="shape-from" value={fromShape} onChange={handleShapeChange}>
                        <option value="rectangular">Rectangular</option>
                        <option value="round">Round</option>
                        <option value="springform">Springform</option>
                        <option value="loaf">Loaf</option>
                        <option value="bundt">Bundt</option>
                        <option value="tube">Tube</option>
                    </select>
                </p>

                <p>
                    <label>Dimensions: </label>
                    <select id="shape-original-dimensions" name="shape-original-dimensions" value={selectedDimensions ? originalDimensions.findIndex(d => d === selectedDimensions) : ''} onChange={handleDimensionChange}>
                        {originalDimensions.map((option, index) => (
                            <option key={index} value={index}>{option.label}</option>
                        ))}
                    </select>
                </p>

                <div className="result">
                    <h3>Equivalent To:</h3>
                    {selectedDimensions && (
                        <p>
                            {Object.entries(selectedDimensions).map(([key, value]) => (
                                <span key={key}>{key}: {value} </span>
                            ))}
                        </p>
                    )}
                </div>
            </form>
        </section>
    );
}
