import { useEffect, useState } from 'react';

const pans = {
    rectangular: [
        { length: 8, width: 8, height: 2 },
        { length: 9, width: 9, height: 2 },
        { length: 10, width: 10, height: 2 },
        { length: 11, width: 7, height: 2 },
        { length: 13, width: 9, height: 2 }
    ],
    round: [
        { diameter: 6, height: 2 },
        { diameter: 9, height: 2 },
        { diameter: 9, height: 2 }
    ],
    springform: [
        { diameter: 9, height: 2.5 },
        { diameter: 10, height: 2.5 }
    ],
    bundt: [
        { diameter: 10, height: 3 }
    ],
    tube: [
        { diameter: 9, height: 3 }
    ],
    loaf: [
        { length: 8, width: 4, height: 3 },
        { length: 9, width: 5, height: 3 }
    ],
};

function cubicInchesToLitres(x) {
    return x * 0.0163871;
}

function calculateRectangleVolume({ length, width, height }) {
    return cubicInchesToLitres(length * width * height);
}

// times .125 floored

export default function PanSizer() {

    const [ fromShape, setFromShape ] = useState('rectangular');
    const [ originalDimensions, setOriginalDimensions ] = useState(dimensionOptions('rectangular'));
    const [ selectedDimensions, setSelectedDimensions ] = useState();

    function dimensionOptions(type) {
        if (type == 'rectangular' || type == 'loaf') {
            return pans[type].map(({ length, width, height }) => ({
                value: `${length}x${width}x${height}`,
                label: `${length} x ${width} x ${height}`
            }));           
        } else {
            return pans[type].map(({ diameter, height }) => ({
                value: `${diameter}x${height}`,
                label: `${diameter} x ${height}`
            }));  
        }
    }

    // function shapeDimensionList1() {
    //     return (
    //         <ul>
    //             {pans.rectangular.map((pan, index) => (
    //                 <li key={index}>{`${pan.length} x ${pan.width} x ${pan.height}`}</li>
    //             ))}
    //         </ul>
    //     );
    // }

    function handleShapeChange(event) {
        const shapeType = event.target.value;
        setFromShape(shapeType);
        setOriginalDimensions(dimensionOptions(shapeType));

        // console.log(fromShape);

        // When shape changes, need to also update dimensions accordingly
        // setSelectedDimensions(pans[shapeType][0]);

        // console.log(selectedDimensions);
    }

    useEffect(() => {
        console.log(fromShape); // Log the updated value of fromShape
    }, [fromShape]); // Run the effect whenever fromShape changes

    function handleDimensionChange(event) {
        setSelectedDimensions(event.target.value);
        // console.log(selectedDimensions);
    }

    function handleConversion(event) {
        event.preventDefault(); 
    }

    return (
        <section>
            <h2 className="form-title">Pan Sizer</h2>
            <form className="basic-form">
                <p>
                    <label>Shape: </label>
                    <select id="shape-from" name="shape-from" value={fromShape} onChange={handleShapeChange}>
                        <option value="rectangular">Rectangular</option>
                        <option value="round">Round</option>
                        <option value="springform">Springform</option>
                        <option value="loaf">Loaf</option>
                        <option value="bundt">Bundt</option>
                        <option value="tube">Tube</option>
                    </select>
                </p>

                <p>
                    <label>Dimensions: </label>
                    <select id="shape-original-dimensions" name="shape-original-dimensions" value={selectedDimensions} onChange={handleDimensionChange}>
                        {originalDimensions.map((option, index) => (
                            <option key={index} value={option.value}>{option.label}</option>
                        ))}
                    </select>
                </p>

                <button onClick={handleConversion}>Calculate</button>

                {/* <div className="result">
                    <h3>Equivalent To:</h3>
                    <p>{selectedDimensions && `Diameter: ${selectedDimensions.diameter}, Height: ${selectedDimensions.height}`}</p>
                </div> */}


            </form>
        </section>
    );
}
